package com.batchmates.android.musicplayer.view.secondactivity;

import android.app.Activity;
import android.content.Intent;
import android.support.v7.widget.RecyclerView;
import android.util.Log;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.TextView;

import com.batchmates.android.musicplayer.R;
import com.batchmates.android.musicplayer.model.MusicPOJO;
import com.bumptech.glide.Glide;

import java.util.ArrayList;
import java.util.List;

/**
 * Created by Android on 8/15/2017.
 */

public class RecyclerAdapeterHelper extends RecyclerView.Adapter<RecyclerAdapeterHelper.ViewHolder> {


    List<MusicPOJO> musicPOJOs =new ArrayList<>();
    private Activity activity;

    public RecyclerAdapeterHelper(List<MusicPOJO> musicPOJOs, Activity activity) {
        this.musicPOJOs = musicPOJOs;
        this.activity=activity;
    }

    @Override
    public ViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {
        View view= LayoutInflater.from(parent.getContext()).inflate(R.layout.recycler_list,parent,false);
        return new ViewHolder(view);
    }

    @Override
    public void onBindViewHolder(ViewHolder holder, int position) {
        final MusicPOJO musicPOJO=musicPOJOs.get(position);
        holder.songName.setText(musicPOJO.getSongName());
        holder.songArtist.setText(musicPOJO.getBandName());
        holder.albumName.setText(musicPOJO.getAlbumName());
        if (musicPOJO.getPicture()==null)
        {
            Glide.with(holder.itemView.getContext()).load("data:image/jpeg;base64,").into(holder.picture);
        }
        else
        {
            Glide.with(holder.itemView.getContext()).asBitmap().load(musicPOJO.getPicture()).into(holder.picture);
        }

        holder.itemView.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                Intent intent=new Intent("CHANGE");
                intent.putExtra("PATH",musicPOJO.getPath());
                Log.d("Recycler", "onClick: "+musicPOJO.getPath());
                intent.putExtra("NAME",musicPOJO.getSongName());
                Log.d("Recycler", "onClick: "+musicPOJO.getSongName());
                view.getContext().sendBroadcast(intent);
                activity.finish();
            }
        });

    }

    @Override
    public int getItemCount() {
        return musicPOJOs.size();
    }

    public class ViewHolder extends RecyclerView.ViewHolder {
        ImageView picture;
        TextView songName;
        TextView songArtist;
        TextView albumName;
        public ViewHolder(View itemView) {
            super(itemView);

            picture=itemView.findViewById(R.id.ivMusicImage);
            songName=itemView.findViewById(R.id.tvSongName);
            songArtist=itemView.findViewById(R.id.tvSongArtist);
            albumName=itemView.findViewById(R.id.tvAlbumName);
        }
    }
}
